package com.upskill.assignment_2;

public class LoopCount_Down {
	
	// *
	// Initialize i = 50 → Start from 50.
	//Condition i >= 1 → Run loop till i reaches 1.
	//Decrement i-- → Reduce i by 1 in each iteration.
	//Print numbers with space (print(i + " ")) → Ensures output is on the same line.

	public static void main(String[] args) {
		
		// Loop from 50 t0 1
		
		for (int i = 50; i >= 1; i--) {
			System.out.print(i + " ");
			
		}
			// TODO Auto-generated method stub

	}

}
