package com.upskill.java_1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Hello World, This is Pascaleen");
		
		int a = 1;
		int b = a*10;
		boolean c = false;

		System.out.println("First Number:");
		System.out.println(a);
		System.out.println("Second number 10 times the first");
		System.out.println(b);
		System.out.println(c);
		
		String city = "East Brunswick";
		String state = "NJ";
		
		System.out.println(city);
		System.out.println(state);
		
		float num = 5;
		float anothernum = num *10;
		
		System.out.println(anothernum);
		
		float d = 10;
		float e = d *10;
		
		System.out.println(e);
		
		long f = 53;
		long g = 60;
		long h = f+g;
		
		System.out.println(h);
		
		short i = 12345;
		short j = (short) (i*234567);
		
		System.out.println(j);
		
		
		int k = 123456789;
		short l = 12345;
		long n = 123456789223344l;
		byte o = 123;
		float p = 1.23456F;
		double q = 1.12345678;
		char s = 'A';
		boolean t = true;
		
		System.out.println(k);
		System.out.println(l);
		System.out.println(n);
		System.out.println(o);
		System.out.println(p);
		System.out.println(q);
		System.out.println(s);
		System.out.println(t);
		
		
		
		

		
		
		
		
		
				
		

	}

}
